var iaudio=document.getElementById("iaudio");var mis=0;function Music(json_data){this.json_data=json_data;this.sign=1;this.playsign=true;this.jsonLength=getJsonLength(json_data)}function getJsonLength(json_data){let jsonLength=0;for(var item in json_data){jsonLength++}return jsonLength}function TimeSpan(){setInterval("process()",1000)}function process(){$("#imusic-bar").css("width",(iaudio.currentTime/iaudio.duration)*100+"%")}Music.prototype.play=function(){$("#imusic-bar").css("width","0");if(this.playsign){$("#btn-play,#btn-pause").toggle();$(".btn-forward,.btn-backward").css("pointer-events","auto");$("#btn-download").css("display","block")}else{$("#btn-stop").hide();$("#btn-pause").show()}let ititle=json_data[this.sign].name+" - "+json_data[this.sign].artist_name;let iurl=json_data[this.sign].url;$("#iaudio").attr("src",iurl);$("#top-cover").attr("src",json_data[this.sign].album_picUrl).addClass("rot");$("#inf").html(ititle);$("#btn-download").attr({"href":iurl,"download":ititle});iaudio.play();TimeSpan()};Music.prototype.stop=function(){this.playsign=false;$("#btn-stop,#btn-pause").toggle();$("#top-cover").removeClass("rot");iaudio.pause()};Music.prototype.keep=function(){$("#btn-stop,#btn-pause").toggle();$("#top-cover").addClass("rot");iaudio.play()};Music.prototype.last=function(){if(this.sign===1){this.sign=this.jsonLength}else{this.sign--}this.playsign=false;this.play()};Music.prototype.next=function(){if(this.sign===this.jsonLength){this.sign=1}else{this.sign++}this.playsign=false;this.play()};$("#btn-play").on("click",function(){music.play()});$("#btn-stop").on("click",function(){music.keep()});$("#btn-pause").on("click",function(){music.stop()});$("#btn-backward").on("click",function(){music.last()});$("#btn-forward").on("click",function(){music.next()});$("#top-cover").on("click",function(){window.open(url="https://music.163.com/artist?id="+json_data[music.sign].artist_id)});iaudio.onended=function(){music.next()};iaudio.onerror=function(){console.log("该音乐可能由于网络波动或者版权限制导致播放失败了，已自动跳至下一首！");music.next()};$('.mini-imusic').on('click',function(){if(mis===0){music.play();mis=1}else if(mis===1){music.stop();mis=2}else{music.keep();mis=1}});
